import React from 'react'

export default function Basket({card}) {
  let totalAmount = 0; 
  return (
    <div>
      {card.map(element => {
        totalAmount = totalAmount + element.price;
        return (
          <div key={element.id}>
            <div className='card-item'>
            <img src={element.image}/>
          <div className='card-item-info'>
            <h1>{element.title}</h1>
            <p>{element.price}</p>
          </div>
          <p>Total amount + {totalAmount}</p>
            </div>
          </div>
        )
      })}
    </div>
  )
}
